function glossary(e){let l=document.querySelectorAll(e+" li .item"),t=document.querySelectorAll(e+" li .item h3"),o=document.querySelectorAll(e+" li .item p");for(let e=0;e<l.length;e++)l[e].addEventListener("click",(function(){t[e].classList.toggle("header-open"),o[e].classList.toggle("paragraph-open")}))}